import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './Detection.css';

function Detection() {
  const [animalCounts, setAnimalCounts] = useState({});
  const [uploadMessage, setUploadMessage] = useState('');
  const [mobileNumber, setMobileNumber] = useState(''); // Added mobile number state
  const [expectedCounts, setExpectedCounts] = useState({ Boomslang: 0, Cobra: 0 }); // Example expected counts state
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing the camera:', error);
        setUploadMessage('Error accessing the camera. Please check your device settings.');
      }
    };

    startCamera();

    const captureFrame = async () => {
      if (videoRef.current && canvasRef.current) {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
        const dataURL = canvas.toDataURL('image/jpeg');
        const blob = await fetch(dataURL).then(res => res.blob());

        const formData = new FormData();
        formData.append('file', blob, 'image.jpg');

        // Add mobile number and expected counts to the form data
        const additionalData = {
          mobile_number: mobileNumber,
          expected_counts: expectedCounts,
        };
        formData.append('form_data', JSON.stringify(additionalData));

        try {
          const res = await axios.post('http://127.0.0.1:5001/detect', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
          });
          setAnimalCounts(res.data || {});
        } catch (error) {
          console.error('Error fetching animal counts:', error);
          setUploadMessage(`Error: ${error.response?.data?.error || error.message}`);
        }
      }
    };

    const interval = setInterval(captureFrame, 2000);

    return () => clearInterval(interval);
  }, [mobileNumber, expectedCounts]); // Added dependencies to capture the latest state

  return (
    <div className="container">
      <div className="camera-preview">
        <video ref={videoRef} autoPlay style={{ width: '100%', height: 'auto' }} />
        <canvas ref={canvasRef} style={{ display: 'none' }} />
      </div>
      <div className="animal-counts">
        <h2>Animal Counts:</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).length === 0 ? (
              <tr><td colSpan="2">No data available</td></tr>
            ) : (
              Object.entries(animalCounts).map(([animal, count]) => (
                <tr key={animal}>
                  <td>{animal}</td>
                  <td>{count}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
        {uploadMessage && <p>{uploadMessage}</p>}
      </div>
      <div className="input-section">
        <input 
          type="text" 
          placeholder="Enter mobile number" 
          value={mobileNumber} 
          onChange={e => setMobileNumber(e.target.value)} 
        />
        {/* Inputs for expected counts can be added here */}
      </div>
    </div>
  );
}

export default Detection;
