import React from "react";
import './AboutUs.css'; // Assuming you'll style it in a separate CSS file

const teamMembers = [
  {
    name: "Mugunthan S",
    role: "Front end",
    link: "https://mugunthan.xyz",
    image: "/Users/mugunthansaravanan/Desktop/Mini-Projects/count/Final/Client/src/images/mugunthan.jpg" // Replace with actual image path
  },
  {
    name: "Madhankumar S",
    role: "Model",
    link: "#",
    image: "/Users/mugunthansaravanan/Desktop/Mini-Projects/count/Final/Client/src/images/madhankumar.png" // Replace with actual image path
  },
  {
    name: "Mohamed Riyas S",
    role: "Back end",
    link: "#",
    image: "/Users/mugunthansaravanan/Desktop/Mini-Projects/count/Final/Client/src/images/riyas.png" // Replace with actual image path
  },
  {
    name: "Karthikeyan S",
    role: "Dataset and Realtime image collection",
    link: "#",
    image: "/path-to-image/karthikeyan.jpg" // Replace with actual image path
  }
];

const AboutUs = () => {
  return (
    <div className="about-container">
      <h1>Meet Our Team</h1>
      <div className="team-grid">
        {teamMembers.map((member, index) => (
          <div className="team-member" key={index}>
            <img src={member.image} alt={member.name} className="profile-pic" />
            <h3>{member.name}</h3>
            <p>{member.role}</p>
            <a href={member.link} target="_blank" rel="noopener noreferrer">
              {member.link !== "#" ? "Website" : ""}
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AboutUs;
